import { DateRangeInput, DateSingleInput, Datepicker } from '@datepicker-react/styled'
import '@trendmicro/react-sidenav/dist/react-sidenav.css';
import React, { Component } from 'react'
import { ThemeProvider } from "styled-components";

export default class History extends Component {
    constructor(props) {
        super(props);
        this.state = {
            startDate: null,
            endDate: null,
            focusedInput: null,
        }
    }
    reducer(state, action) {
        switch (action.type) {
            case 'focusChange':
                return { ...state, focusedInput: action.payload }
            case 'dateChange':
                return action.payload
            default:
                throw new Error()
        }
    }

    // App() {
    //     const [state, dispatch] = useReducer(reducer, initialState)

    render() {
        return (
            <div>
                <ThemeProvider
                    theme={{
                        breakpoints: ["32em", "48em", "64em"],
                        reactDatepicker: {
                            daySize: [36, 40],
                            fontFamily: "system-ui, -apple-system",
                            colors: {
                                accessibility: "#D80249",
                                selectedDay: "#f7518b",
                                selectedDayHover: "#F75D95",
                                primaryColor: "#d8366f"
                            }
                        }
                    }}
                >
                    <DateRangeInput

                        startDate={this.state.startDate} // Date or null
                        endDate={this.state.endDate} // Date or null
                        focusedInput={this.state.focusedInput} // START_DATE, END_DATE or null
                    />
                </ThemeProvider>
            </div>
        );
    }


}
