import React, { Component } from 'react'
import {
    Container, Col, Form,
    FormGroup, Label, Input,
    Button, Card, CardBody, Link,
} from 'reactstrap';
export default class Login extends Component {
    constructor(props) {
        super(props);
        this.props.logout();

        this.state = {
            username: '',
            password: '',
            submitted: false,
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(e) {
        const { email, value } = e.target;
        this.setState({ [email]: value });
    }

    handleSubmit(e) {
        e.preventDefault();

        this.setState({ submitted: true });
        const { username, password } = this.state;
        if (username && password) {
            this.props.login(username, password);
        }
    }
    render() {
        return (
            <div className="col-md-6 col-md-offset-3"
                style={{
                    marginLeft: '50vh',
                    alignItems: 'center',
                    justifyContent: 'space-between'
                }} >
                <h2 style={{
                    color: "#ff9505",
                    fontWeight: "bold",

                }}>Login</h2>
                <Form name="form" onSubmit={this.handleSubmit} >
                    <FormGroup >
                        <Label for="exampleEmail">Email</Label>
                        <Input type="email" name="email" id="exampleEmail" onChange={this.handleChange} />
                        {submitted && !username &&
                            <div>email is required</div>
                        }
                    </FormGroup>
                    <FormGroup >
                        <Label for="examplePassword">Password</Label>
                        <Input type="password" name="password" id="examplePassword" onChange={this.handleChange} />
                        {submitted && !password &&
                            <div className="help-block">Password is required</div>
                        }
                    </FormGroup>
                    <FormGroup >
                        <br />
                        <Button color="success">Login</Button>
                        <Button color="link">Register</Button>

                    </FormGroup>
                </Form>
            </div>
        );
    }
}
